pub fn part_1() {
    let input = include_str!("inputs/day01.txt");

    let floor = input.chars().fold(
        0,
        |acc, instruction| {
            if instruction == '(' { acc + 1 } else { acc - 1 }
        },
    );

    println!("{}", floor);
}

